import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable} from 'rxjs';
import { Person } from '../models/models';
import { AppConfig } from '../config/api-server';

@Injectable({
  providedIn: 'root'
})
export class PersonDataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  constructor(private httpC:HttpClient) { }

  getData():Observable<any>{
    //return this.httpC.get('./assets/data/about.json');
    return this.httpC.get<Person>(AppConfig.API_SERVER_GET+"person", this.httpHeaderOptions);
  };
  addPerson(body:Person):Observable<any>{
    return this.httpC.post(AppConfig.API_SERVER_NEW+"person", body, { withCredentials: true });
  }
}
