import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; 
import { Router } from '@angular/router';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-form-education',
  templateUrl: './form-education.component.html',
  styleUrls: ['./form-education.component.css']
})

export class FormEducationComponent implements OnInit{
  Form:FormGroup;

  constructor(
  private formBuilder:FormBuilder,
  private dataService:DataService,
  private ruta:Router){
    this.Form=this.formBuilder.group(
      {
        //aca.....
        form_title:["",[Validators.required]],
        form_description:["",[Validators.required]],
        form_period:["",[Validators.required]],
        deviceInfo:this.formBuilder.group({
          deviceId:[""],
          deviceType:["DEVICE_TYPE_ANDROID"],
          notificationToken:[""]
        })
      }
    )
  }
  ngOnInit(): void {}

  get Title(){
    return this.Form.get("form_title");
  }
  get Description(){
    return this.Form.get("form_description");
  }
  get Period(){
    return this.Form.get("form_period");
  }

  onSend(event:Event){
    event.preventDefault;
    /*this.autenticationService.IniciarSesion(this.Form.value).subscribe((data:any)=>{
      console.log("DATA:" + JSON.stringify(data));
      this.ruta.navigate(['/portfolio']);
    })*/
  }
}
