import { Component, OnInit, Input } from '@angular/core';
import { Person } from 'src/app/models/models';
import { PersonDataService } from 'src/app/services/person-data.service';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html'
})
export class AboutComponent implements OnInit {
  @Input() reloadHTML: () => void = () => {this.getData();}; // inicialización por defecto

  data: Person = {
    id: 0,
    name: '',
    lastname: '',
    long_description: '',
    short_description: '',
    title: '',
    nationality: '',
    birthdate: '',
    img_profile: '',
    img_banner: '',
    email: '',
    phone: '',
    facebook_id: '',
    instagram_id: '',
    twitter_id: ''
  };
  age:any;
  //private birth:any;

  constructor(private dataService:PersonDataService){}

  ngOnInit(): void {
    this.getData();
  }
  public getData(){
    this.dataService.getData().subscribe({
      next: (resp) => {
        this.data = resp[0];
        //console.log("Main -> Datos Personales: "+JSON.stringify(this.data));
        console.log("About -> OK");
        this.age = Math.floor(((Date.now() - Date.parse(this.data.birthdate)) / (1000 * 3600 * 24))/365);
      },
      error: (error) => {
        console.error(error);
      }
    });
  }
}
<section id="About" class="row">
    <h3>SOBRE MI</h3>
    <div class="col-12 mt-4">
        <div class="card bg-transparent mb-4">
            <app-manage-buttons class="pRelative"
                [button_edit]="true"
                [id]="0"
                [modal_id_edit]="'aboutModal'">
            </app-manage-buttons>
            <div class="row g-0">
                <div class="img-profile col-xs-12 col-sm-4 col-md-4 col-xl-2 rounded-circle pRelative">
                    <img class="card-img p-2 rounded-circle" [src]="data.img_profile" alt="">
                    <!--app-manage-buttons [button_edit]="true"></app-manage-buttons-->
                </div>
                <div class="col-xs-12 col-sm-8 col-md-8 col-xl-10 ">
                    <div class="card-body">
                        <h4 class="card-title" >Soy {{data.name}}</h4>
                        <p class="card-text">Tengo {{age}} años y soy {{data.nationality}}</p>
                        <p class="card-text">{{data.long_description}}</p>
                        <p class="card-text">¿Tienes un proyecto interesante? <a [routerLink]="['/contact']">Hablemos!</a></p>
                    </div>
                </div>
            </div>
        </div>
    </div>
</section>
<app-form-about
    [modalId]="'aboutModal'"
    [modalTitle]="'Editar Entrada'"
    [reloadHTML] = "reloadHTML"

    [defaultName]="data.name"
    [defaultLastName]="data.lastname"
    [defaultTitle]="data.title"
    [defaultLongDescription]="data.long_description"
    [defaultShortDescription]="data.short_description"
    [defaultNationality]="data.nationality"
    [defaultBirthDate]="data.birthdate"
    [defaultPfp]="data.img_profile"
    [defaultBanner]="data.img_banner"
    [defaultEmail]="data.email"
    [defaultPhone]="data.phone"
    [defaultFacebook]="data.facebook_id"
    [defaultInstagram]="data.instagram_id"
    [defaultTwitter]="data.twitter_id">
</app-form-about>
