import { Component, OnInit } from '@angular/core';
import { EducationDataService } from 'src/app/services/education-data.service';

@Component({
  selector: 'app-education-list',
  templateUrl: './education-list.component.html',
  styleUrls: ['./education-list.component.css']
})
export class EducationListComponent implements OnInit{

  data:any;

  constructor(private educationDataService:EducationDataService){}

  ngOnInit(): void {
    this.educationDataService.getData().subscribe(educationDataService => {
      this.data = educationDataService.education;
    });
  }

  ngOnInit(): void {
    this.educationDataService.getData().subscribe({
      next: (resp) => {
        this.data = resp[0];
        console.log("EducationList -> Education: "+JSON.stringify(this.data));
      },
      error: (error) => {
        console.error(error);
      }
    });
  }
}
