import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable} from 'rxjs';
import { Education } from '../models/models';
import { AppConfig } from '../config/api-server';

@Injectable({
  providedIn: 'root'
})
export class EducationDataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  private API_SERVER = "http://localhost:8080/api";
  private API_SERVER_GET = this.API_SERVER+"/get";

  constructor(private httpC:HttpClient) { }

  getEducationData():Observable<any>{
    return this.httpC.get<Education>(AppConfig.API_SERVER_GET+"education", this.httpHeaderOptions);
  }
  deleteEducation(id:number):Observable<any>{
    return this.httpC.delete(this.API_SERVER+"delete_education/"+id);
  }
  addEducation(body:Education):Observable<any>{
    return this.httpC.post(this.API_SERVER+"new_education", body, { withCredentials: true });
  }
}
