import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpResponse } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs';
import { Credentials } from '../models/auth-model';


@Injectable({
  providedIn: 'root'
})
export class AuthenticationDataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  private API_SERVER = "http://localhost:8080/";

  currentUserSubject: BehaviorSubject<any>;

  constructor(private httpC:HttpClient) {
    console.log("El servicio de autenticacion está corriendo")
    this.currentUserSubject=new BehaviorSubject<any>(JSON.parse(sessionStorage.getItem('currentUser')|| '{}'))
  }
  /*logIn(credenciales:Credentials):Observable<any>{
    return this.httpC.post(this.API_SERVER+"login", credenciales).pipe(map(data=>{
      sessionStorage.setItem('currentUser', JSON.stringify(data));
      this.currentUserSubject.next(data);
      return data;
    }))
  }*/
  logIn(creds:Credentials):Observable<any>{
    return this.httpC.post(this.API_SERVER+"login", creds, {
      observe: "response"
    }).pipe(map((response: HttpResponse<any>) => {
      const body = response.body;
      const header = response.headers;

      const bearerToken = headers.get('Authorization')!;
      const token = bearerToken.replace('Bearer ', '');

      localStorage.setItem('token', token);

      this.currentUserSubject.next(data);
      return data;
    }))
  }
  get userAuth(){
    return this.currentUserSubject.value;
  }
}
