import { Component, OnInit, ElementRef, ViewChild, ViewContainerRef, ComponentRef , ChangeDetectorRef} from '@angular/core';

import { DataService } from 'src/app/services/data.service';
import { EducationItemComponent } from '../education-item/education-item.component';

@Component({
  selector: 'app-education-list',
  templateUrl: './education-list.component.html',
  styleUrls: ['./education-list.component.css']
})
export class EducationListComponent implements OnInit {
  componentes: ComponentRef<any>[] = [];
  @ViewChild('educationList', { read: ViewContainerRef, static: true }) educationList!: ViewContainerRef;

  data: EducationItemComponent[];
  componentFactoryResolver: any;

  constructor(
    private dataService:DataService,
    private elRef: ElementRef,
    private cdRef: ChangeDetectorRef){}


  ngOnInit(): void {
    this.dataService.getEducationData().subscribe({
      next: (resp) => {
        this.data = resp;
        console.log("EducationList -> Education: "+JSON.stringify(this.data));
      },
      error: (error) => {
        console.error(error);
      }
    });
  }

  public qweqwe(): void {
    this.dataService.getEducationData().subscribe({
      next: (resp) => {
        this.data = resp;
        console.log("EducationList -> Education: "+JSON.stringify(this.data));
      },
      error: (error) => {
        console.error(error);
      }
    })
  }

  deleteItem(itemDelete:EducationItemComponent, domIndex:number){
    this.dataService.deleteEducation(itemDelete.id).subscribe(resp=>{
      console.log(resp);
        this.dataService.getEducationData().subscribe({
          next: (resp) => {
            this.data = resp;
            console.log("EducationList -> Education: "+JSON.stringify(this.data));
          },
          error: (error) => {
            console.error(error);
          }
        })
        //this.cdRef.detectChanges(); // Detecta los cambios y actualiza el DOM
    });

  }
}
