import { Component, OnInit } from '@angular/core';
import { DataService } from 'src/app/services/data.service';


@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent implements OnInit {

  data:any;
  private age:any;
  private birth:any;

  constructor(private dataService:DataService){}

  ngOnInit(): void {
    this.dataService.getData().subscribe(dataService => {
      this.data = dataService;
      //this.birth = new Date();
      this.birth = Date.parse(this.data.date_of_birth);
      //this.age = new Date();
    });
  }

  getAge(){
    Math.floor(((Date.now() - this.birth) / (1000 * 3600 * 24))/365);
    return Date.now() - this.birth;
  }
}
<div id="About" class="row g-5 mt-5">
    <h3>SOBRE MI</h3>
    <div class="col-12 mt-4">
        <div class="card bg-transparent mb-4">
            <div class="row g-0">
                <div class="col-4 col-xl-2">
                    <img class="card-img p-2" [src]="'../../assets/img/'+data.img_profile_small" alt="">
                </div>
                <div class="col-8 col-xl-10 ">
                    <div class="card-body">
                        <h4 class="card-title" >{{data.name}}</h4>
                        <p class="card-text">Edad: {{getAge()}}</p>
                        <p class="card-text">{{data.location}}</p>
                        <div class="row mb-5 card-text">
                            <div class="col-2 col-sm-2 col-lg-1 col-xl-1 social face"><a class="fa-brands fa-facebook-f" [href]="data.facebook" target="_blank"></a></div>
                            <div class="col-2 col-sm-2 col-lg-1 col-xl-1 social twit"><a class="fa-brands fa-twitter" [href]="data.twitter" target="_blank"></a></div>
                            <div class="col-2 col-sm-2 col-lg-1 col-xl-1 social inst"><a class="fa-brands fa-instagram" [href]="data.instagram" target="_blank"></a></div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
