import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable} from 'rxjs';
import { WorkProject } from '../models/models';
import { AppConfig } from '../config/api-server';

@Injectable({
  providedIn: 'root'
})
export class WorkProjectDataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  private API_SERVER = "http://localhost:8080/";

  constructor(private httpC:HttpClient) { }

  // GETTERS ===========================================================================
  getWorkProjectData():Observable<any>{
    return this.httpC.get<WorkProject>(this.API_SERVER+"get_workprojects", this.httpHeaderOptions);
  }
  deleteWorkProject(id:number):Observable<any>{
    return this.httpC.delete(this.API_SERVER+"delete_workproject/"+id);
  }
  addWorkProject(body:WorkProject):Observable<any>{
    return this.httpC.post(this.API_SERVER+"new_workproject", body, { withCredentials: true });
  }
}
