import { Component, OnInit } from '@angular/core';
import { DataService } from 'src/app/services/data.service';
import { AppComponent } from '../../app.component';


@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})

export class MainComponent implements OnInit {

  data:any;

  constructor(private dataService:DataService,private appComponent:AppComponent){}

  ngOnInit(): void {
    this.data = appComponent.getDataJson();
    /*this.dataService.getData().subscribe(dataService => {
      this.data = dataService[0];
    });*/
    this.dataService.getData().subscribe({
      next: (resp) => {
        this.data = resp[0];
        console.log("Main -> Datos Personales: "+JSON.stringify(this.data));
      },
      error: (error) => {
        console.error(error);
      }
    });
    console.log("DATA: "+this.data);
  }
}
