import { Component, OnInit } from '@angular/core';
//import { Session } from 'src/app/config/config-data'
import { AuthenticationDataService } from './services/authentication-data.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent{

  title:string = 'portfolio';
  IsLogin:string = "";

  SessionValid:Boolean = false;

  constructor(private dataService:AuthenticationDataService){}

  ngOnInit() {
    this.SessionValid = this.dataService.isSessionValid();
    if(this.SessionValid){
      this.SessionValid = true;
      this.IsLogin = "logged";
      sessionStorage.setItem('Session', this.SessionValid.toString());
      console.log("BBBBBBBBBBBBBBBBBBBB "+this.IsLogin);

    }
    else{
      this.SessionValid = false;
      //localStorage.removeItem("token");
      //sessionStorage.removeItem("Session");
      console.log("AAAAAAAAAAAAAAAAAAAAAAA FALSE");

    }
    console.log("////////////////////////////////////"+this.dataService.isSessionValid());
  }
}
