import { Component, OnInit, ElementRef, ViewChild, ViewContainerRef, ComponentRef } from '@angular/core';

import { DataService } from 'src/app/services/data.service';
import { EducationItemComponent } from '../education-item/education-item.component';

@Component({
  selector: 'app-education-list',
  templateUrl: './education-list.component.html',
  styleUrls: ['./education-list.component.css']
})
export class EducationListComponent implements OnInit {

  data:any;

  constructor(private dataService:DataService, private elRef: ElementRef,){}

  ngOnInit(): void {
    this.dataService.getEducationData().subscribe({
      next: (resp) => {
        this.data = resp;
        console.log("EducationList -> Education: "+JSON.stringify(this.data));
      },
      error: (error) => {
        console.error(error);
      }
    });
  };
  delete(item:EducationItemComponent) {
    // código de la función aquí
    this.dataService.deleteEducation(item.id).subscribe(resp=>{
      console.log(resp);
    });
    if (item.elRef && item.elRef.nativeElement) {
      this.elRef.nativeElement.removeChild(item.elRef.nativeElement);
      this.cdRef.detectChanges();
    }

  }
}
