import { Component, ViewChild, ComponentRef , ComponentFactoryResolver, AfterViewInit} from '@angular/core';
import { DynamicComponentDirective } from 'src/app/directives/dynamic-component.directive';
import { DataService } from 'src/app/services/data.service';
import { SkillItemComponent } from '../skill-item/skill-item.component';

@Component({
  selector: 'app-skill-list',
  templateUrl: './skill-list.component.html',
  styleUrls: ['./skill-list.component.css']
})
export class SkillListComponent implements AfterViewInit{
  @ViewChild(DynamicComponentDirective) dynamic !:DynamicComponentDirective;

  //data:any;
  data_frontend:any;
  data_backend:any;
  data_others:any;
  data_soft:any;
  data_languages:any;

  childComponents_frontend: ComponentRef<any>[] = [];

  constructor(private dataService:DataService){}

  ngAfterViewInit(): void {
    // HARD SKILLS
    this.dataService.getHardSkillData().subscribe(data => {
      this.data_frontend = data.filter((data: { category: string; }) => data.category === "Frontend");
      this.data_backend = data.filter((data: { category: string; }) => data.category === "Backend");
      this.data_others = data.filter((data: { category: string; }) => data.category === "Others");
      this.generateChildComponents(this.data_frontend);
    });
    // SOFT SKILLS
    this.dataService.getSoftSkillData().subscribe(data => {
      this.data_soft = data.filter((data: { category: string; }) => data.category === "Soft");
      this.data_languages = data.filter((data: { category: string; }) => data.category === "Language");
    });
  };

  generateChildComponents(data:[]):void {
    const viewContainerRef = this.dynamic.viewContainerRef;
    data.forEach((child: {id: number, skillname: string, value: string}) => {
      const containerRef = viewContainerRef.createComponent<any>(SkillItemComponent);
      containerRef.instance.id = child.id;
      containerRef.instance.skillname = child.skillname;
      containerRef.instance.value = child.value;
      this.deleteItem = this.deleteItem.bind(this); ///////*
      containerRef.instance.onDelete = this.deleteItem;
      this.childComponents_frontend.push(containerRef);
    });
  }

  deleteItem(id:number){
    this.dataService.deleteHardSkill(id).subscribe(resp=>{
      console.log("deleteHardSkill(): ERROR -> "+resp);
      // Filtro el array de componentes para encontrar el índice del componente con el id especificado
      const index = this.childComponents_frontend.findIndex(c => c.instance.id === id);
      // Si se encontró el componente, lo elimino
      if (index !== -1) {
        this.childComponents_frontend[index].destroy();
        this.childComponents_frontend.splice(index, 1);
      }
    });
  }
}
