import { Component, OnInit } from '@angular/core';
//import { Session } from 'src/app/config/config-data'
import { AuthenticationDataService } from './services/authentication-data.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  title:string = 'portfolio';

  IsLogin:string = "";

  SessionValid:Boolean = false;

  constructor(private dataService:AuthenticationDataService){

  }

  ngOnInit(): void {
    this.dataService.isSessionValid().subscribe((isValid: boolean) => {

      this.SessionValid = isValid;

      console.log("Sesión válida: " + isValid);

      if(isValid){
        this.IsLogin = "logged";
        console.log("BBBBBBBBBBBBBBBBBBBB "+this.IsLogin);
      }
      else{
        //localStorage.removeItem("token");
        //sessionStorage.removeItem("Session");
        console.log("AAAAAAAAAAAAAAAAAAAAAAA FALSE");

      }
      sessionStorage.removeItem('Session');
      sessionStorage.setItem('Session', isValid.toString());
    });
  }
}
