import { Component, Input, ElementRef, ViewChild, ViewContainerRef, ComponentRef , ChangeDetectorRef, Host, OnInit} from '@angular/core';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-education-item',
  templateUrl: './education-item.component.html',
  styleUrls: ['./education-item.component.css']
})

export class EducationItemComponent implements OnInit{
  constructor(
      @Host() private parentComponent: ParentComponent,
      private dataService:DataService,
      private elRef: ElementRef,) {}

  @Input() id: number = 0;
  @Input() title: string = "title here";
  @Input() description: string = "description here";
  @Input() period: string = "period here";
  @Input() icon: string = "";
  @Input() dom_index: number = 0;

  @Input() Delete: () => void = () => {
    this.onDelete(this, this.dom_index);
  }; // inicialización por defecto
  @Input() onDelete: (item:EducationItemComponent, index:number) => void = (item, index) => {
    this.elRef.nativeElement.deleteItem(this, this.dom_index);

  };
  //@Input() onDelete: () => void = () => {};
}
