import { Component, OnInit, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; 
import { Router } from '@angular/router';
import { DataService } from 'src/app/services/data.service';



@Component({
  selector: 'app-form-skill',
  templateUrl: './form-skill.component.html'
})

export class FormSkillComponent implements OnInit{
  @Input() type:String = "";

  Form:FormGroup;

  constructor(
  private formBuilder:FormBuilder,
  private dataService:DataService,
  private ruta:Router){
    this.Form=this.formBuilder.group(
      {
        //aca.....
        form_skillname:["",[Validators.required]],
        form_category:["",[Validators.required]],
        form_value:["",[Validators.required]],
        deviceInfo:this.formBuilder.group({
          deviceId:[""],
          deviceType:["DEVICE_TYPE_ANDROID"],
          notificationToken:[""]
        })
      }
    )
  }
  ngOnInit(): void {}

  get SkillName(){
    return this.Form.get("form_skillname");
  }
  get Category(){
    return this.Form.get("form_category");
  }
  get Value(){
    return this.Form.get("form_value");
  }
  get FormValid(){
    return (!this.Form.get("form_skillname")?.errors && !this.Form.get("form_category")?.errors && !this.Form.get("form_value")?.errors);
  }

  onSend(event:Event){
    event.preventDefault();

    const body = {
        //id:0,
        skilname:this.Form.get('form_skillname')?.value,
        category:this.Form.get('form_category')?.value,
        value:this.Form.get('form_value')?.value
      }
    this.dataService.addSkill(this.type, body).subscribe((data:any)=>{
      console.log("DATA:" + JSON.stringify(data));
    })
  }
}
