import { Component, Input } from '@angular/core';
import { DataService } from 'src/app/services/data.service';


@Component({
  selector: 'app-manage-buttons',
  templateUrl: './manage-buttons.component.html',
  styleUrls: ['./manage-buttons.component.css']
})
export class ManageButtonsComponent {
  @Input() id: number = 0;

  constructor(private dataService:DataService){}

  public deleteEducation(id:number){
    this.dataService.deleteEducation(id).subscribe(resp=>{
      console.log(resp);
    });
  }

}
