import { Component, Input } from '@angular/core';
import { DataService } from 'src/app/services/data.service';
import { ViewRef } from '@angular/core'; // Importa el servicio ViewRef


@Component({
  selector: 'app-education-item',
  templateUrl: './education-item.component.html',
  styleUrls: ['./education-item.component.css']
})
export class EducationItemComponent {
  constructor(private dataService:DataService, private viewRef: ViewRef) {}



  @Input() id: number = 0;
  @Input() title: string = "title here";
  @Input() description: string = "description here";
  @Input() period: string = "period here";
  @Input() icon: string = "";

  onClick() {
    // código de la función aquí
    this.dataService.deleteEducation(this.id).subscribe(resp=>{
      console.log(resp);
    });
    //this.viewRef.destroy();
  }
}
