import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  private API_SERVER = "http://localhost:8080/";

  //url:string="http://localhost:8080/";

  constructor(private httpC:HttpClient) { }

  // GETTERS ===========================================================================
  getData():Observable<any>{
    //return this.httpC.get('./assets/data/about.json');
    return this.httpC.get<any>(this.API_SERVER+"get_person", this.httpHeaderOptions);
  };
  getEducationData():Observable<any>{
    return this.httpC.get<any>(this.API_SERVER+"get_education", this.httpHeaderOptions);
  }
  getHardSkillData():Observable<any>{
    return this.httpC.get<any>(this.API_SERVER+"get_hardskill", this.httpHeaderOptions);
  };
  getSoftSkillData():Observable<any>{
    return this.httpC.get<any>(this.API_SERVER+"get_softskill", this.httpHeaderOptions);
  };
  // DELETERS ==========================================================================
  deleteEducation(id:number):Observable<any>{
    return this.httpC.delete(this.API_SERVER+"delete_education/"+id);
  }
  deleteHardSkill(id:number):Observable<any>{
    return this.httpC.delete(this.API_SERVER+"delete_hardskill/"+id);
  }
  deleteSoftSkill(id:number):Observable<any>{
    return this.httpC.delete(this.API_SERVER+"delete_softskill/"+id);
  }
  // SAVERS ============================================================================
  addEducation():Observable<any>{
    return this.httpC.delete(this.API_SERVER+"new_education/"+id);
  }
}
