import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable} from 'rxjs';
import { Skill } from '../models/models';
import { AppConfig } from '../config/api-server';

@Injectable({
  providedIn: 'root'
})
export class SkillDataService {

  private httpHeaderOptions = {
    headers: new HttpHeaders({
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS, HEAD'
    })
  };

  constructor(private httpC:HttpClient) { }

  getHardSkillData():Observable<any>{
    return this.httpC.get<Skill>(AppConfig.API_SERVER_GET+"hardskill", this.httpHeaderOptions);
  };
  getSoftSkillData():Observable<any>{
    return this.httpC.get<Skill>(AppConfig.API_SERVER_GET+"softskill", this.httpHeaderOptions);
  };

  deleteHardSkill(id:number):Observable<any>{
    return this.httpC.delete(AppConfig.API_SERVER_DELETE+"hardskill/"+id);
  }
  deleteSoftSkill(id:number):Observable<any>{
    return this.httpC.delete(AppConfig.API_SERVER_DELETE+"softskill/"+id);
  }

  addHardSkill(body:Skill):Observable<any>{
      return this.httpC.post(AppConfig.API_SERVER_NEW+"hardskill", body, { withCredentials: true });
  }
  addSoftSkill(body:Skill):Observable<any>{
    return this.httpC.post(AppConfig.API_SERVER_NEW+"softskill", body, { withCredentials: true });
}
}
