import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest} from '@angular/common/http';
import { Observable } from 'rxjs';
import { AuthenticationDataService } from 'src/app/services/authentication-data.service';


@Injectable({
  providedIn: 'root'
})
export class InterceptorService implements HttpInterceptor{

  constructor(private autenticationService:AuthenticationDataService) { }
  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    var accessToken = this.autenticationService.getToken();

    if(req.url.indexOf('/api/get/') > -1){
      req=req.clone({
        setHeaders:{
          'Access-Control-Allow-Origin': 'http://localhost:4200',
          'Access-Control-Allow-Methods': 'GET',
          'Access-Control-Allow-Headers': '*',
          'Content-Type': 'application/json'
        }
      })
    }   
    else if(req.url.indexOf('/api/auth/') > -1 && accessToken !== undefined){
      req=req.clone({
        setHeaders:{
          //'Access-Control-Allow-Origin': '*',
          'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE',
          //'Content-Type': 'application/json',
          //'Access-Control-Allow-Headers': 'Origin, Content-Type, Accept, Authorization, X-Request-With',
          Authorization: 'Bearer ${accessToken}'
        }
      })
    }
    console.log("Interceptor esta corriendo: "+accessToken);
    //console.trace();
    return next.handle(req);
  }
}
